import { createContext, useContext, useEffect, useReducer } from "react";
import axios from 'axios';

const ContextGlobal = createContext();

export const initialState = {
  theme: "", 
  data: [],
  dentist: {},
  favs: []
}

const reducer = (state, action) => {
  switch(action.type){
    case 'GET_LIST':
      return {...state, data: action.payload}
    case 'GET_DENTIST':
      return {...state, dentist: action.payload}
    case 'ADD_FAV':
      return 
    default: 
      throw new Error();
  }
}

export const ContextProvider = ({ children }) => {
  const [dentistState, dentistDispatch] = useReducer(reducer, initialState);
  const url = 'https://jsonplaceholder.typicode.com/users'

  useEffect(() => {
    axios(url)
    .then(res => dentistDispatch({type: 'GET_LIST', payload: res.data}))
    .catch(err => console.log(err))
  }, [])

  return (
    <ContextGlobal.Provider value={{
      dentistState, dentistDispatch
    }}>
      {children}
    </ContextGlobal.Provider>
  );
};

export const useDentistState = () => useContext(ContextGlobal)
