import React from "react";
import { Link } from 'react-router-dom'
import { useDentistState } from './utils/global.context'

const Card = ({ id, name, username }) => {

  const { dentistState, favDispatch } = useDentistState();

  const addFav = () => {
   favDispatch({type: 'ADD_FAV', payload: dentistState.dentist})
  }

  return (
    <div className="card">
      <div>
          <Link to={`/dentist/${id}`}>
            <h5>{name}</h5>
            <p>User: {username}</p>
        </Link>
        <button onClick={() => addFav()} className="favButton">Add fav</button>
      </div>
    </div>
  );
};

export default Card;
