import { createContext, useContext, useEffect, useReducer } from "react";
import axios from 'axios';
import { fetchReducer, favReducer, themeReducer } from './reducers';

const ContextGlobal = createContext();

const initialStateFetch = { data: [], dentist: {} }

const initialStateFav = JSON.parse(localStorage.getItem('favs')) || [];

const initialStateTheme = localStorage.getItem('theme') || [];

export const ContextProvider = ({ children }) => {
  //***** Fetch Dentist Data From API *****//
  const [dentistState, dentistDispatch] = useReducer(fetchReducer, initialStateFetch);

  const url = 'https://jsonplaceholder.typicode.com/users'
  useEffect(() => {
    axios(url)
    .then(res => dentistDispatch({type: 'GET_LIST', payload: res.data}))
    .catch(err => console.log(err))
  }, [])

  //***** Favs *****//
  const [favState, favDispatch] = useReducer(favReducer, initialStateFav);

  useEffect(() => {
    localStorage.setItem('favs', JSON.stringify(favState))
  }, [favState])
 
  //***** Theme *****//
  const [themeState, themeDispatch] = useReducer(themeReducer, initialStateTheme);

  return (
    <ContextGlobal.Provider value={{
      dentistState, dentistDispatch,
      favState, favDispatch
    }}>
      {children}
    </ContextGlobal.Provider>
  );
};

export const useDentistState = () => useContext(ContextGlobal)
