import React from "react";
import { Link } from 'react-router-dom'
import { useDentistState } from './utils/global.context'

const Card = () => {

  const { dentistState, favDispatch } = useDentistState();

  const addFav = (dentist) => {
   favDispatch({type: 'ADD_FAV', payload: dentist})
  }

  return (
    <div className="card">
      {dentistState.data.map(dentist => (
        <div key={dentist.id}>
          <Link to={`/dentist/${dentist.id}`}>
            <h5>{dentist.name}</h5>
            <p>{dentist.username}</p>
          </Link>
          <button onClick={() => addFav(dentist)} className="favButton">Add fav</button>
        </div>
      ))}
    </div>
  );
};

export default Card;
