import React, { useState } from "react";
import { Link } from 'react-router-dom'
import { useDentistState } from './utils/global.context'

const Card = ({ id, name, username }) => {

  const { favDispatch } = useDentistState();

  const { isFavorited, setIsFavorited } = useState(false);

  const toggleFav = (e) => {
    e.preventDefault();
    if (isFavorited) {
      favDispatch({ type: 'DELETE_FAV', payload: id })
    } else {
      favDispatch({ type: 'ADD_FAV', payload: {id, name, username} })
    }

    setIsFavorited
  }


  return (
    <div className="card">
      <div>
        <Link to={`/dentist/${id}`}>
          <p>User: {username}</p>
          <h5>{name}</h5>
        </Link>
        <button onClick={toggleFav} className="favButton">Agregar a Favoritos</button>
      </div>
    </div>
  );
};

export default Card;
