import React from "react";
import { Link } from 'react-router-dom'
import { useDentistState } from './utils/global.context'

const Card = ({name, username, id}) => {

  const { dentistState, dentistDispatch } = useDentistState();

  const addFav = () => {
    dentistDispatch({type: 'ADD_FAV', payload: dentistState.dentist})
    localStorage.setItem(dentistState.dentist.idid, JSON.stringify(dentistState.dentist))
  }

  return (
    <div className="card">
      {dentistState.data.map(dentist => (
        <div key={dentist.id}>
          <Link to={`/dentist/${dentist.id}`}>
            <h5>{dentist.name}</h5>
            <p>{dentist.username}</p>
          </Link>
          <button onClick={addFav} className="favButton">Add fav</button>
        </div>
      ))}
    </div>
  );
};

export default Card;
